import { Component, Input, OnInit } from '@angular/core';
import { NodeMapComponent } from '../node-map/node-map.component';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss']
})
export class FooterComponent implements OnInit {

  @Input() nodeMapComponent: NodeMapComponent;

  algorithmService: any;

  getMessage(): string {
    let finishReason = this.algorithmService?.checkIfDone.reason;
    if (this.algorithmService.isRunning) {
      return 'Visualization in progress...';
    } else if (finishReason == "reached end") {
      return `Reached end after ${this.algorithmService.iterationCount}`;
    } else if (finishReason == "no solution") {
      return `No solution was found after ${this.algorithmService.iterationCount}`;
    } else {
      return 'Algorithm is Idle';
    }
  }

  ngOnInit() {
    this.algorithmService = this.nodeMapComponent.algorithmService;
  }
}
