import { Injectable } from '@angular/core';
import { Node } from 'src/app/node/node';
import { Pathfinder } from './pathfinder';

@Injectable({
  providedIn: 'root'
})
// bidirectional swarm works the same way as dijkstra
export class BidirectionalService extends Pathfinder {

  currentNodeFromEnd: Node;
  unvisitedNodesFromEnd: Node[];

  override setAlgorithmValues = (start: Node, end: Node) => {
    // set everything the same way as other algorithms
    super.setAlgorithmValues(start, end);
    this.endingNode.pathDistance = 0;
    this.currentNodeFromEnd = undefined;
    this.unvisitedNodesFromEnd = [end];
  };

  override doIteration = (): void => {
    // this block ensures that the algorithm goes back and forth 
    // from searching from the start to end
    let currentList: Node[];
    if (this.iterationCount % 2 == 0) {
      currentList = this.unvisitedNodes;
      var [index, closestNode] = this.getClosestNodeFromList(currentList);
      this.visitNodeFromStart(closestNode);
      this.currentNode = closestNode;
      closestNode.isVisited = true;
    } else {
      currentList = this.unvisitedNodesFromEnd;
      var [index, closestNode] = this.getClosestNodeFromList(currentList);
      this.visitNodeFromEnd(closestNode);
      this.currentNodeFromEnd = closestNode;
      closestNode.isVisitedFromEnd = true;
    }

    currentList.splice(index, 1);

    this.iterationCount += 1;
  }

  getClosestNodeFromList = (nodeList: Node[]) => {
    let closestNode = nodeList[0];
    let closestNodeIndex = undefined;

    for (let [index, node] of nodeList.entries()) {
      if (node.pathDistance < closestNode.pathDistance) {
          closestNode = node;
          closestNodeIndex = index;
      }
    }

    return [closestNodeIndex, closestNode];
  }

  visitNodeFromStart = (nodeToVisit: Node) => {
    let unvisitedNeighbours = this.getUnvisitedNeighbours(nodeToVisit);

    for (let { node: neighbour, relativeDistance } of unvisitedNeighbours) {
      let potentialDistance = nodeToVisit.pathDistance + relativeDistance;

      if (neighbour.pathDistance > potentialDistance) {
        neighbour.pathDistance = potentialDistance;
        neighbour.parent = nodeToVisit;

        this.unvisitedNodes.push(neighbour);
        neighbour.isProspected = true;
      }
    }

    nodeToVisit.isVisited = true;
  }

  visitNodeFromEnd = (nodeToVisit: Node) => {
    let unvisitedNeighbours = this.getUnvisitedFromEndNeighbours(nodeToVisit);

    for (let { node: neighbour, relativeDistance } of unvisitedNeighbours) {
      let potentialDistance = nodeToVisit.pathDistance + relativeDistance;

      if (neighbour.pathDistance > potentialDistance) {
        neighbour.pathDistance = potentialDistance;
        neighbour.parent = nodeToVisit;

        this.unvisitedNodesFromEnd.push(neighbour);
        neighbour.isProspected = true;
      }
    }

    nodeToVisit.isVisitedFromEnd = true;
  }

  getUnvisitedFromEndNeighbours = (node: Node) => {
    let nodeNeighbours = node.neighbours

    return [...nodeNeighbours.filter((neighbour) => !neighbour.node.isBlocked && !neighbour.node.isVisitedFromEnd)];
  };

  override checkIfDone = (): { isDone: boolean, reason?: string } => { 
    if (this.currentNode.isVisitedFromEnd) {
      return { isDone: true, reason: 'reached end' };
    } else if (this.unvisitedNodes.length == 0 &&
       this.unvisitedNodesFromEnd.length == 0) {
      return { isDone: true, reason: 'no solution' };
    } else {
      return { isDone: false };
    }
  }

  override tracePath = (nodeToTrace?: Node): Node[] => {
    let pathNodes = [];

    nodeToTrace = this.startingNode;
    while (nodeToTrace.parent) {
      pathNodes.push(nodeToTrace);
      nodeToTrace = nodeToTrace.parent;
    }

    nodeToTrace = this.endingNode;
    while (nodeToTrace.parent) {
      pathNodes.push(nodeToTrace);
      nodeToTrace = nodeToTrace.parent;
    }

    return pathNodes;
  };
}
